//create TearDown script
testRunner.testCase.testSuite.project.getTestSuiteByName('TestSuite 1').getTestCaseByName('TestCase 1').setTearDownScript('log.info "teardown for Test1"')

//to GET the SetUp and TearDown scripts
log.info("script: "+testRunner.testCase.getSetupScript());
log.info("script: "+testRunner.testCase.getTearDownScript());

con:configuration.scriptText:
//check the response time (in milliseconds)
assert messageExchange.timeTaken <400

//check for Endpoint
log.info messageExchange.getEndpoint()//print endpoint
def endpoint="http://webservices.oorsprong.org/websamples.countryinfo/CountryInfoService.wso"
assert messageExchange.getEndpoint() == endpoint

//check for header
log.info (messageExchange.responseHeaders["Content-Length"]);
assert messageExchange.responseHeaders["Content-Length"] != null;

//check for attachments
assert messageExchange.responseAttachments.length == 0;

//validate response nodes
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requsetHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
def refNum = responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName")
assert responseHolder.getNodeValue("//m:CountryCurrencyResult/m:sName") == "Rupees"

//to get response
def resp = messageExchange.responseContentAsXml.toString()
log.info(resp)

//get json response import groovy.json.JsonSlurper def responseMessage = messageExchange.response.responseContent def json = new JsonSlurper().parseText(responseMessage)
//assert node values log.info json.name assert json.capital.toString() != null assert json.name.toString() == "[Taiwan]"
testStepName = messageExchange.modelItem.testStep.name//to get the Test Step Name log.info testStepName xmlHold = messageExchange.responseContentAsXml.toString() //to store the response as Xml string

con:soapui-project.con:afterLoadScript:
def su = new File("C:/Users/Avinav/Desktop/SOAP UI Projects/setup.txt").text
def td = new File("C:/Users/Avinav/Desktop/SOAP UI Projects/teardown.txt").text 

//to get the setup and teardown during project load
project.getTestSuiteList().each{
	testSuite -> testSuite.getTestCaseList().each{
	testCase -> testCase.setSetupScript(su)
			  testCase.setSetupScript(td)

	}
